// Process the Equinix Fabric connections response
const response = $input.first();
const responseData = response?.json; // FIXED: Access json property
const connections = responseData?.data || [];
const processedConnections = [];

// Handle case where connections array is empty
if (connections.length === 0) {
  console.log('No connections found');
  return {
    summary: {
      totalConnections: 0,
      healthyConnections: 0,
      downConnections: 0,
      transitioningConnections: 0,
      unknownConnections: 0,
      lastUpdated: new Date().toISOString(),
      message: 'No active connections found'
    },
    connections: [],
    alertsRequired: [],
    pagination: responseData?.pagination || null
  };
}

for (const connection of connections) {
  const processedConnection = {
    uuid: connection.uuid,
    name: connection.name,
    type: connection.type,
    state: connection.state,
    operationalStatus: connection.operation?.equinixStatus || connection.operation?.opStatus,
    providerStatus: connection.operation?.providerStatus,
    bandwidth: connection.bandwidth,
    aSide: {
      accessPoint: connection.aSide?.accessPoint?.type,
      location: connection.aSide?.accessPoint?.location?.metroCode || connection.aSide?.accessPoint?.location?.metroName,
      port: connection.aSide?.accessPoint?.port?.uuid,
      vlan: connection.aSide?.accessPoint?.linkProtocol?.vlanTag
    },
    zSide: {
      accessPoint: connection.zSide?.accessPoint?.type,
      location: connection.zSide?.accessPoint?.location?.metroCode || connection.zSide?.accessPoint?.location?.metroName,
      port: connection.zSide?.accessPoint?.port?.uuid,
      vlan: connection.zSide?.accessPoint?.linkProtocol?.vlanTag
    },
    account: {
      accountNumber: connection.account?.accountNumber,
      organizationName: connection.account?.organizationName
    },
    createdDateTime: connection.changeLog?.createdDateTime,
    lastUpdatedDateTime: connection.changeLog?.updatedDateTime,
    healthStatus: determineHealthStatus(connection)
  };

  processedConnections.push(processedConnection);
}

// Function to determine overall health status
function determineHealthStatus(connection) {
  const state = connection.state?.toLowerCase();
  const equinixStatus = connection.operation?.equinixStatus?.toLowerCase();
  const providerStatus = connection.operation?.providerStatus?.toLowerCase();

  if (state === 'provisioned' && equinixStatus === 'provisioned') {
    return 'healthy';
  } else if (state === 'provisioning' || state === 'deprovisioning') {
    return 'transitioning';
  } else if (state === 'deprovisioned' || equinixStatus === 'down' || providerStatus === 'down') {
    return 'down';
  } else {
    return 'unknown';
  }
}

// Calculate summary statistics
const summary = {
  totalConnections: processedConnections.length,
  healthyConnections: processedConnections.filter(c => c.healthStatus === 'healthy').length,
  downConnections: processedConnections.filter(c => c.healthStatus === 'down').length,
  transitioningConnections: processedConnections.filter(c => c.healthStatus === 'transitioning').length,
  unknownConnections: processedConnections.filter(c => c.healthStatus === 'unknown').length,
  lastUpdated: new Date().toISOString(),
  pagination: responseData?.pagination || null
};

return {
  summary: summary,
  connections: processedConnections,
  alertsRequired: processedConnections.filter(c => c.healthStatus === 'down' || c.healthStatus === 'unknown')
};

// COMPLETE WORKING VERSION - Process all ports
const input = $input.first();
const responseData = input?.json;
const ports = responseData?.data || [];

console.log(`Processing ${ports.length} ports`);

if (ports.length === 0) {
  return {
    portSummary: { totalPorts: 0, activePorts: 0, inactivePorts: 0, highUtilizationPorts: 0, fullyUtilizedPorts: 0, unusedPorts: 0, averageUtilization: 0, totalBandwidth: 0, usedBandwidth: 0, lastUpdated: new Date().toISOString() },
    ports: [],
    highUtilizationPorts: [],
    fullyUtilizedPorts: [],
    pagination: responseData?.pagination
  };
}

const processedPorts = [];
let activePorts = 0;
let highUtilPorts = 0;
let fullyUtilPorts = 0;
let unusedPorts = 0;
let totalBandwidth = 0;
let totalUsedBandwidth = 0;
let totalUtilization = 0;

// Process all ports
for (let i = 0; i < ports.length; i++) {
  const port = ports[i];

  // Use actual API field names
  const bandwidth = port.bandwidth || 0;
  const usedBandwidth = port.usedBandwidth || 0;
  const availableBandwidth = port.availableBandwidth || 0;

  // Calculate bandwidth utilization
  const utilization = bandwidth > 0 ? ((usedBandwidth / bandwidth) * 100) : 0;

  // Connection counts
  const connectionCount = port.operation?.connectionCount || 0;
  const evplVCCount = port.operation?.evplVCCount || 0;
  const accessVCCount = port.operation?.accessVCCount || 0;

  // Count for summary
  if (port.state === 'ACTIVE') activePorts++;
  if (utilization > 80) highUtilPorts++;
  if (utilization >= 100) fullyUtilPorts++;
  if (usedBandwidth === 0) unusedPorts++;

  // Accumulate totals
  totalBandwidth += bandwidth;
  totalUsedBandwidth += usedBandwidth;
  totalUtilization += utilization;

  // Store processed port data
  processedPorts.push({
    uuid: port.uuid,
    name: port.name,
    type: port.type,
    state: port.state,
    bandwidth: bandwidth,
    usedBandwidth: usedBandwidth,
    availableBandwidth: availableBandwidth,
    utilization: parseFloat(utilization.toFixed(2)),
    connectionCount: connectionCount,
    evplVCCount: evplVCCount,
    accessVCCount: accessVCCount,
    location: {
      metroCode: port.location?.metroCode || '',
      metroName: port.location?.metroName || '',
      ibx: port.location?.ibx || ''
    },
    account: {
      organizationName: port.account?.organizationName || '',
      accountName: port.account?.accountName || ''
    },
    physicalPortsSpeed: port.physicalPortsSpeed || 0,
    physicalPortsType: port.physicalPortsType || '',
    packageType: port.package?.code || ''
  });
}

const portSummary = {
  totalPorts: responseData?.pagination?.total || ports.length,
  activePorts: activePorts,
  inactivePorts: ports.length - activePorts,
  highUtilizationPorts: highUtilPorts,
  fullyUtilizedPorts: fullyUtilPorts,
  unusedPorts: unusedPorts,
  averageUtilization: ports.length > 0 ? (totalUtilization / ports.length).toFixed(2) : 0,
  totalBandwidth: totalBandwidth,
  usedBandwidth: totalUsedBandwidth,
  availableBandwidth: totalBandwidth - totalUsedBandwidth,
  lastUpdated: new Date().toISOString()
};

console.log('Port processing complete:');
console.log('- Total ports:', portSummary.totalPorts);
console.log('- Active ports:', portSummary.activePorts);
console.log('- High utilization ports:', portSummary.highUtilizationPorts);
console.log('- Average utilization:', portSummary.averageUtilization + '%');

return {
  portSummary: portSummary,
  ports: processedPorts,
  highUtilizationPorts: processedPorts.filter(p => p.utilization > 80),
  fullyUtilizedPorts: processedPorts.filter(p => p.utilization >= 100),
  pagination: responseData?.pagination
};

// ENHANCED Generate Status Report - Handles partial data gracefully
const inputs = $input.all();

console.log('=== ENHANCED DIAGNOSTIC ===');
console.log('Number of inputs received:', inputs.length);

let connectionData = null;
let portData = null;

// Check each input with detailed logging
for (let i = 0; i < inputs.length; i++) {
  const input = inputs[i];
  const data = input.json || input;

  console.log('Input ' + i + ' keys:', Object.keys(data));
  console.log('Input ' + i + ' preview:', JSON.stringify(data).substring(0, 200) + '...');

  // Check for connection data with multiple patterns
  if (data.summary && data.connections) {
    connectionData = data;
    console.log('‚úÖ Found connection data (pattern 1): ' + data.summary.totalConnections + ' connections');
  } else if (data.summary && data.summary.totalConnections) {
    connectionData = data;
    console.log('‚úÖ Found connection data (pattern 2): ' + data.summary.totalConnections + ' connections');
  } else if (data.alertsRequired) {
    connectionData = data;
    console.log('‚úÖ Found connection data (pattern 3): alerts array found');
  }

  // Check for port data with multiple patterns  
  if (data.portSummary && data.ports) {
    portData = data;
    console.log('‚úÖ Found port data (pattern 1): ' + data.portSummary.totalPorts + ' ports');
  } else if (data.portSummary && data.portSummary.totalPorts) {
    portData = data;
    console.log('‚úÖ Found port data (pattern 2): ' + data.portSummary.totalPorts + ' ports');
  } else if (data.highUtilizationPorts) {
    portData = data;
    console.log('‚úÖ Found port data (pattern 3): high utilization array found');
  }
}

// Try alternative methods to get connection data
if (!connectionData) {
  console.log('üîç Trying alternative methods for connection data...');

  try {
    // Method 1: Try to get from Process Connection Status node directly
    const connectionNodes = $('Process Connection Status').all();
    console.log('Connection nodes found:', connectionNodes.length);

    if (connectionNodes && connectionNodes.length > 0) {
      for (let j = 0; j < connectionNodes.length; j++) {
        const nodeData = connectionNodes[j].json || connectionNodes[j];
        console.log('Connection node ' + j + ' keys:', Object.keys(nodeData));

        if (nodeData.summary && (nodeData.connections || nodeData.summary.totalConnections > 0)) {
          connectionData = nodeData;
          console.log('‚úÖ Retrieved connection data from node reference method 1');
          break;
        }
      }
    }
  } catch (error) {
    console.log('‚ùå Method 1 failed:', error.message);
  }

  try {
    // Method 2: Try different node reference syntax
    const connectionNodeAlt = $('Process Connection Status');
    if (connectionNodeAlt && connectionNodeAlt.json) {
      const nodeData = connectionNodeAlt.json;
      if (nodeData.summary) {
        connectionData = nodeData;
        console.log('‚úÖ Retrieved connection data from node reference method 2');
      }
    }
  } catch (error) {
    console.log('‚ùå Method 2 failed:', error.message);
  }
}

console.log('=== FINAL DATA STATUS ===');
console.log('Connection data available:', !!connectionData);
console.log('Port data available:', !!portData);

if (connectionData) {
  console.log('Connections - Total:', connectionData.summary ? connectionData.summary.totalConnections : 'unknown');
  console.log('Connections - Down:', connectionData.summary ? connectionData.summary.downConnections : 'unknown');
} else {
  console.log('‚ö†Ô∏è No connection data - will calculate health based on ports only');
}

if (portData) {
  console.log('Ports - Total:', portData.portSummary ? portData.portSummary.totalPorts : 'unknown');
  console.log('Ports - High Util:', portData.portSummary ? portData.portSummary.highUtilizationPorts : 'unknown');
}

// Enhanced health calculation that works with partial data
function calculateFlexibleHealth(connectionSummary, portSummary) {
  let totalScore = 0;
  let maxScore = 0;
  const factors = {};

  // Connection health (if available)
  if (connectionSummary && connectionSummary.totalConnections > 0) {
    const downConnections = connectionSummary.downConnections || 0;
    const connectionHealth = ((connectionSummary.totalConnections - downConnections) / connectionSummary.totalConnections) * 100;
    factors.connectionHealth = connectionHealth;
    totalScore += connectionHealth * 0.6; // Higher weight when it's the only factor
    maxScore += 60;
    console.log('Connection health factor:', connectionHealth.toFixed(1) + '%');
  } else {
    console.log('Connection health: Not available');
  }

  // Port utilization health (if available)
  if (portSummary && portSummary.totalPorts > 0) {
    const highUtilPorts = portSummary.highUtilizationPorts || 0;
    const fullyUtilPorts = portSummary.fullyUtilizedPorts || 0;

    // Calculate port health based on utilization
    const utilizationPenalty = (highUtilPorts * 0.5 + fullyUtilPorts * 1.0) / portSummary.totalPorts;
    const portHealth = Math.max(0, (1 - utilizationPenalty) * 100);

    factors.portHealth = portHealth;
    totalScore += portHealth * 0.4;
    maxScore += 40;
    console.log('Port health factor:', portHealth.toFixed(1) + '%');
  } else {
    console.log('Port health: Not available');
  }

  const overallScore = maxScore > 0 ? Math.round(totalScore / maxScore * 100) : 0;

  // Determine status with more nuanced thresholds
  let status = 'unknown';
  if (maxScore === 0) {
    status = 'unknown';
  } else if (overallScore >= 90) {
    status = 'healthy';
  } else if (overallScore >= 75) {
    status = 'warning';
  } else if (overallScore >= 50) {
    status = 'degraded';
  } else {
    status = 'critical';
  }

  return {
    status: status,
    score: overallScore,
    factors: factors,
    dataAvailable: {
      connections: !!connectionSummary,
      ports: !!portSummary
    }
  };
}

// Generate concerns based on available data
function generateFlexibleConcerns(connectionSummary, portSummary) {
  const concerns = [];

  if (!connectionSummary && !portSummary) {
    concerns.push('No monitoring data available');
    return concerns;
  }

  if (!connectionSummary) {
    concerns.push('Connection monitoring data unavailable');
  } else {
    if (connectionSummary.downConnections > 0) {
      concerns.push(connectionSummary.downConnections + ' connections are down');
    }
    if (connectionSummary.unknownConnections > 0) {
      concerns.push(connectionSummary.unknownConnections + ' connections have unknown status');
    }
  }

  if (!portSummary) {
    concerns.push('Port monitoring data unavailable');
  } else {
    if (portSummary.highUtilizationPorts > 5) { // Only flag if significant number
      concerns.push(portSummary.highUtilizationPorts + ' ports are over 80% utilized');
    }
    if (portSummary.fullyUtilizedPorts > 0) {
      concerns.push(portSummary.fullyUtilizedPorts + ' ports are fully utilized');
    }
  }

  return concerns;
}

// Calculate health with flexible inputs
const overallHealth = calculateFlexibleHealth(
  connectionData ? connectionData.summary : null,
  portData ? portData.portSummary : null
);

const primaryConcerns = generateFlexibleConcerns(
  connectionData ? connectionData.summary : null,
  portData ? portData.portSummary : null
);

console.log('=== FINAL HEALTH CALCULATION ===');
console.log('Overall health:', overallHealth.status);
console.log('Health score:', overallHealth.score);
console.log('Primary concerns:', primaryConcerns.length);

// Return comprehensive result that works with partial data
return {
  overallHealth: overallHealth,
  primaryConcerns: primaryConcerns,
  timestamp: new Date().toISOString(),

  summary: {
    totalConnections: connectionData && connectionData.summary ? connectionData.summary.totalConnections : 0,
    healthyConnections: connectionData && connectionData.summary ? connectionData.summary.healthyConnections : 0,
    downConnections: connectionData && connectionData.summary ? connectionData.summary.downConnections : 0,
    unknownConnections: connectionData && connectionData.summary ? connectionData.summary.unknownConnections : 0,

    totalPorts: portData && portData.portSummary ? portData.portSummary.totalPorts : 0,
    activePorts: portData && portData.portSummary ? portData.portSummary.activePorts : 0,
    highUtilizationPorts: portData && portData.portSummary ? portData.portSummary.highUtilizationPorts : 0,
    fullyUtilizedPorts: portData && portData.portSummary ? portData.portSummary.fullyUtilizedPorts : 0,
    averageUtilization: portData && portData.portSummary ? portData.portSummary.averageUtilization : 0
  },

  criticalConnections: connectionData && connectionData.alertsRequired ? connectionData.alertsRequired.slice(0, 10) : [],
  criticalPorts: portData && portData.fullyUtilizedPorts ? portData.fullyUtilizedPorts.slice(0, 10) : [],

  // Enhanced debugging info
  dataSourcesAvailable: {
    hasConnectionData: !!connectionData,
    hasPortData: !!portData,
    inputCount: inputs.length,
    nodeReferences: {
      connectionNodeAccessible: 'tested',
      portNodeAccessible: 'tested'
    }
  }
};

// COMPLETE MANUAL EMAIL FORMATTER
// Replace your Summarization Chain with this Code node

const input = $input.first().json;

console.log('=== MANUAL EMAIL FORMATTER ===');
console.log('Input data available:', !!input);
console.log('Health status:', input.overallHealth?.status);
console.log('Health score:', input.overallHealth?.score);

// Extract data with safe defaults
const health = input.overallHealth || {};
const summary = input.summary || {};
const concerns = input.primaryConcerns || [];
const criticalConnections = input.criticalConnections || [];
const criticalPorts = input.criticalPorts || [];
const timestamp = input.timestamp || new Date().toISOString();

// Status-based configuration
const statusConfig = {
  'critical': {
    emoji: 'üö®',
    urgency: 'URGENT',
    priority: 'high',
    actionTime: 'IMMEDIATE (1-2 Hours)',
    color: 'red'
  },
  'degraded': {
    emoji: '‚ö†Ô∏è',
    urgency: 'HIGH PRIORITY',
    priority: 'high',
    actionTime: 'PRIORITY (4-8 Hours)',
    color: 'orange'
  },
  'warning': {
    emoji: '‚ö†Ô∏è',
    urgency: 'ATTENTION REQUIRED',
    priority: 'normal',
    actionTime: 'REVIEW (24 Hours)',
    color: 'yellow'
  },
  'healthy': {
    emoji: '‚úÖ',
    urgency: 'INFORMATIONAL',
    priority: 'low',
    actionTime: 'ROUTINE MONITORING',
    color: 'green'
  },
  'unknown': {
    emoji: '‚ùì',
    urgency: 'DATA ISSUE',
    priority: 'normal',
    actionTime: 'INVESTIGATE (2-4 Hours)',
    color: 'gray'
  }
};

const config = statusConfig[health.status] || statusConfig['unknown'];

// Generate email subject
const emailSubject = `${config.emoji} ${config.urgency}: Equinix Fabric Alert - ${(health.status || 'UNKNOWN').toUpperCase()} (${health.score || 0}/100)`;

// Helper function to format numbers
function formatNumber(num) {
  return (num || 0).toLocaleString();
}

// Helper function to calculate percentages
function calculatePercentage(part, total) {
  if (!total || total === 0) return '0';
  return Math.round((part / total) * 100);
}

// Connection health summary
const connectionHealthPercent = calculatePercentage(summary.healthyConnections, summary.totalConnections);
const connectionDownPercent = calculatePercentage(summary.downConnections, summary.totalConnections);

// Port utilization summary
const portUtilizationPercent = calculatePercentage(summary.highUtilizationPorts, summary.totalPorts);
const portSaturationPercent = calculatePercentage(summary.fullyUtilizedPorts, summary.totalPorts);

// Generate main email body
const emailBody = `${config.emoji} EQUINIX FABRIC INFRASTRUCTURE ALERT
${'='.repeat(50)}
Status: ${(health.status || 'UNKNOWN').toUpperCase()}
Health Score: ${health.score || 0}/100
Priority: ${config.urgency}
Generated: ${new Date(timestamp).toLocaleString('en-AU', { timeZone: 'Australia/Sydney' })}

üìä EXECUTIVE SUMMARY
${'='.repeat(50)}
${health.status === 'critical' ? 
  `üö® CRITICAL INFRASTRUCTURE ISSUES DETECTED
- ${summary.downConnections || 0} connections are offline (${connectionDownPercent}% failure rate)
- ${summary.fullyUtilizedPorts || 0} ports at maximum capacity
- Immediate intervention required across multiple regions` :

  health.status === 'degraded' || health.status === 'warning' ?
  `‚ö†Ô∏è INFRASTRUCTURE DEGRADATION DETECTED
- ${summary.downConnections || 0} connections experiencing issues
- ${summary.highUtilizationPorts || 0} ports approaching capacity limits
- Performance monitoring and capacity planning recommended` :

  health.status === 'healthy' ?
  `‚úÖ INFRASTRUCTURE OPERATING NORMALLY
- All critical systems functioning within normal parameters
- Routine monitoring continues as scheduled
- No immediate action required` :

  `‚ùì MONITORING DATA INCOMPLETE
- Unable to determine complete infrastructure health status
- Data collection or connectivity issues detected
- Investigation of monitoring systems recommended`
}

üìà INFRASTRUCTURE METRICS
${'='.repeat(50)}
CONNECTIONS:
  ‚Ä¢ Total: ${formatNumber(summary.totalConnections)}
  ‚Ä¢ Healthy: ${formatNumber(summary.healthyConnections)} (${connectionHealthPercent}%)
  ‚Ä¢ Down: ${formatNumber(summary.downConnections)} (${connectionDownPercent}%)
  ‚Ä¢ Unknown Status: ${formatNumber(summary.unknownConnections)}

PORTS:
  ‚Ä¢ Total Active: ${formatNumber(summary.totalPorts)}
  ‚Ä¢ Average Utilization: ${summary.averageUtilization || 0}%
  ‚Ä¢ High Utilization (>80%): ${formatNumber(summary.highUtilizationPorts)} (${portUtilizationPercent}%)
  ‚Ä¢ Fully Utilized (100%): ${formatNumber(summary.fullyUtilizedPorts)} (${portSaturationPercent}%)

${concerns.length > 0 ? `‚ö†Ô∏è PRIMARY CONCERNS
${'='.repeat(50)}
${concerns.map((concern, index) => `${index + 1}. ${concern}`).join('\n')}` : '‚úÖ NO CRITICAL CONCERNS IDENTIFIED'}

${criticalConnections.length > 0 ? `üî¥ CRITICAL CONNECTIONS REQUIRING ATTENTION
${'='.repeat(50)}
${criticalConnections.slice(0, 8).map((conn, index) => {
  const location = conn.aSide?.location || conn.location || 'Unknown';
  const accountInfo = conn.account?.accountNumber ? ` [Account: ${conn.account.accountNumber}]` : '';
  return `${index + 1}. ${conn.name}
   Status: ${conn.state} (${conn.healthStatus})
   Location: ${location}${accountInfo}
   Created: ${conn.createdDateTime ? new Date(conn.createdDateTime).toLocaleDateString() : 'Unknown'}`;
}).join('\n\n')}

${criticalConnections.length > 8 ? `... and ${criticalConnections.length - 8} additional critical connections` : ''}` : ''}

${criticalPorts.length > 0 ? `üü° HIGH-UTILIZATION PORTS REQUIRING REVIEW
${'='.repeat(50)}
${criticalPorts.slice(0, 8).map((port, index) => {
  const location = port.location?.metroCode || port.location?.metroName || 'Unknown';
  const utilizationWarning = port.utilization >= 100 ? ' ‚ö†Ô∏è FULLY SATURATED' : port.utilization >= 90 ? ' ‚ö†Ô∏è NEAR CAPACITY' : '';
  return `${index + 1}. ${port.name}
   Utilization: ${port.utilization}%${utilizationWarning}
   Location: ${location}
   Bandwidth: ${port.bandwidth ? formatNumber(port.bandwidth) + ' Mbps' : 'Unknown'}`;
}).join('\n\n')}

${criticalPorts.length > 8 ? `... and ${criticalPorts.length - 8} additional high-utilization ports` : ''}` : ''}

üìã RECOMMENDED ACTIONS
${'='.repeat(50)}
${config.actionTime}:

${health.status === 'critical' ? 
  `üö® IMMEDIATE ACTIONS REQUIRED:
- Contact Network Operations Center (NOC) immediately
- Investigate ${summary.downConnections || 0} offline connections in affected regions
- Review capacity for ${summary.fullyUtilizedPorts || 0} saturated ports
- Coordinate with regional teams in affected locations
- Prepare incident communication for stakeholders
- Consider emergency capacity provisioning if needed

üìÖ SHORT-TERM ACTIONS (24-48 Hours):
- Root cause analysis for connection failures
- Capacity expansion planning for over-utilized ports
- Update incident response procedures
- Review monitoring thresholds and alerting rules` :

  health.status === 'degraded' || health.status === 'warning' ?
  `‚ö†Ô∏è PRIORITY ACTIONS RECOMMENDED:
- Monitor ${summary.downConnections || 0} problematic connections closely
- Review capacity planning for ${summary.highUtilizationPorts || 0} high-utilization ports
- Contact vendors for affected regions if issues persist
- Update capacity forecasts and procurement timelines

üìÖ ONGOING MONITORING (Next 7 Days):
- Daily review of utilization trends
- Proactive capacity management
- Performance baseline updates
- Preventive maintenance scheduling` :

  health.status === 'healthy' ?
  `‚úÖ ROUTINE ACTIVITIES:
- Continue standard monitoring procedures
- Review monthly capacity reports
- Update performance baselines
- Conduct scheduled maintenance activities

üìÖ STRATEGIC PLANNING:
- Long-term capacity planning review
- Infrastructure optimization opportunities
- Performance trend analysis` :

  `‚ùì INVESTIGATION REQUIRED:
- Verify monitoring system connectivity
- Check data collection processes
- Review API authentication and permissions
- Validate monitoring configuration

üìÖ SYSTEM HEALTH CHECK:
- Test all monitoring endpoints
- Verify alert distribution lists
- Update monitoring documentation`
}

üîß SUPPORT CONTACTS
${'='.repeat(50)}
- Network Operations Center: [Your NOC Contact]
- Regional Support Teams: [Contact Information]
- Escalation Manager: [Manager Contact]
- Emergency Hotline: [Emergency Number]

For technical details and live dashboard access:
- Monitoring Dashboard: [Your Dashboard URL]
- Incident Management: [Your Ticket System]

${'='.repeat(50)}
Generated by Equinix Fabric Monitoring System
Next scheduled check: ${new Date(Date.now() + 15 * 60 * 1000).toLocaleString('en-AU', { timeZone: 'Australia/Sydney' })}

This is an automated alert. Reply to this email or contact the NOC for immediate assistance.`;

// Log email details for debugging
console.log('Email subject:', emailSubject);
console.log('Email priority:', config.priority);
console.log('Concerns count:', concerns.length);
console.log('Critical connections:', criticalConnections.length);
console.log('Critical ports:', criticalPorts.length);

// Return complete email data
return {
  // Email configuration
  subject: emailSubject,
  message: emailBody,
  priority: config.priority,

  // Additional metadata for email node configuration
  urgencyLevel: config.urgency,
  healthStatus: health.status,
  healthScore: health.score,
  timestamp: timestamp,

  // Summary for other nodes if needed
  alertSummary: {
    totalIssues: concerns.length,
    criticalConnections: criticalConnections.length,
    criticalPorts: criticalPorts.length,
    overallSeverity: health.status,
    requiresImmediateAction: health.status === 'critical'
  }
};
